import Sidebar from "./Sidebar";
import React from "react";

const person1 = new Sidebar.ContactClass('Flinn', 2)
const person2 = new Sidebar.ContactClass('Alex', 1)
const contacts = [person1, person2]

class ClassicMessage {
    from: string;
    to: string;
    text: string;

    constructor() {
    }
}

class ChatWindow extends React.Component<Array<object>, {messages: object}> {
    UpdateTimer: NodeJS.Timer | undefined = undefined
    constructor(args: any) {
        super(args);
        this.state = {messages: this.fetch_messages()}
    }

    componentDidMount() {
        this.UpdateTimer = setInterval(() => this.update_messages(),500);
    }

    componentWillUnmount() {
        clearInterval(this.UpdateTimer)
    }


    update_messages() {
        this.setState({messages: this.fetch_messages()});
    }

    fetch_messages() {
        const msgs: Array<any> = [
        {
            'from': "flinnfx#101",
            'to': 'tcgamer#102',
            'message': 'This is some random junk'
        },
        {
            'from': 'flinnfx#101',
            'to': 'tcgamer#102,',
            'message': 'way more junken text'
        }];
        return msgs
    }

    render() {
        const messages = this.state.messages
        return (
            <div>
                {messages.map()}
            </div>
        )
    }

}

export default ChatWindow
